pub fn install() {
    Agent::new("pacman")
    .acmd("game_attack11", pac_jab1, Priority::Low)    
    .acmd("game_attack12", pac_jab2, Priority::Low)    
    .acmd("game_attack13", pac_jab3, Priority::Low)    
    .install();
}

